import io from "socket.io-client";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/esm/Container";
import Row from "react-bootstrap/esm/Row";
import InputGroup from 'react-bootstrap/InputGroup';
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';

// const socketIO = io.connect("http://localhost:8080");

// socketIO.on("connect", () => {
//   console.log("Connected");
// });

// function sendMessage() {
//   socketIO.emit("send_message", { data: "Hello!"} );
// }

function MessageBox() {
    return (
        <Container className="border border-2 flex-grow message-box">
            <Row>
                <p>I am placholder</p>
            </Row>
            <Row className="message-input">
                <InputGroup>
                    <Form.Control
                        placeholder="Message..."
                        aria-label="Recipient's username"
                        aria-describedby="basic-addon2"
                        className="shadow-none"
                    />
                    <Button variant="outline-secondary" id="button-addon2">Send</Button>
                </InputGroup>
            </Row>
        </Container>
    );
}

export default MessageBox
